CREATE TABLE smartap.T_ADDRESS (F_ADDRESS_ID varchar(32) PRIMARY KEY, F_AREA_ID varchar(32), F_USER_ID varchar(32), F_DETAIL_ADDRESS varchar(1000) NOT NULL, F_ADDRESSTR varchar(3200), F_AREA_CODE numeric(38, 0) NOT NULL, F_TYPE numeric(2, 0)) DISTRIBUTED BY (F_ADDRESS_ID);

CREATE TABLE smartap.T_AP_EP_ASSOCIATED (ASSOCIATED_ID varchar(32) PRIMARY KEY, TIMESTAP timestamp(6) NOT NULL, DEVICE_MAC varchar(32) NOT NULL, DEVICE_CODE varchar(32) NOT NULL, AP_MAC varchar(32) NOT NULL, AP_SSID varchar(64), EP_MACS text) DISTRIBUTED BY (ASSOCIATED_ID);

CREATE TABLE smartap.T_COLLECTION_HOTSPOT (F_ID varchar(32) PRIMARY KEY, F_HOTSPOT_MAC varchar(20) NOT NULL, F_HOTSPOT_SSID varchar(256), F_HOTSPOT_CHANNEL varchar(6) NOT NULL, F_HOTSPOT_ENCRYPTION_TYPE varchar(2) NOT NULL, F_CAP_TIME timestamp(6) NOT NULL, F_SIGNAL varchar(60) NOT NULL, F_X varchar(8), F_Y varchar(8), F_SITE_CODE varchar(14) NOT NULL, F_DECIVE_CODE varchar(21) NOT NULL, F_LOCATION_ID varchar(32) NOT NULL, F_LONGITUDE varchar(20) NOT NULL, F_LATITUDE varchar(20) NOT NULL, F_HAS_FULLINDEX numeric(8, 0) DEFAULT 0, F_AREA_CODE numeric(38,0) NOT NULL, F_CREATE_TIME timestamp(6), F_ORG_CODE varchar(60) NOT NULL) DISTRIBUTED BY (F_ID);

CREATE TABLE smartap.T_DEVICE (F_DEVICE_ID varchar(32) PRIMARY KEY, F_DEVICE_CODE varchar(30) NOT NULL, F_USER_ID varchar(32) NOT NULL, F_MANUFACTURER_ID varchar(32), F_DEVICE_NAME varchar(60), F_DESCRIPTION varchar(100), F_BUY_DATE timestamp(6), F_STATUS numeric(38, 0) NOT NULL, F_SERVER_STATUS numeric(38, 0) NOT NULL DEFAULT 4, F_DATA_STATUS numeric(38, 0) NOT NULL DEFAULT 6, F_TYPE numeric(38, 0) NOT NULL, F_MAC varchar(20), F_LASTEST_REBOOT_TIME varchar(50), F_VERSION varchar(50), F_CAP_RADIUS numeric, F_CAP_INTERVAL numeric, F_CREATION_TIME timestamp(6) NOT NULL, F_DEVICE_IP varchar(60), F_CAP_DATA_TYPE varchar(100), F_DEVICE_WORK_MODEL numeric, F_SYNC_DATA_INTERVAL numeric, F_START_VSSID numeric, F_HARD_WARE_VERSION varchar(30), F_SOFT_WARE_VERSION varchar(30), F_LIMIT_RATE numeric, F_VSSID_FLOW numeric, F_ISDEL numeric, F_AREA_CODE numeric(38, 0), F_NET_MODE numeric, F_THREE_IN_ONE numeric DEFAULT 0, F_SSID_1 varchar(200), F_SSID_2 varchar(200), F_SSID_3 varchar(200), F_SSID_4 varchar(200), F_SSID_5 varchar(200), UPDATE_URL varchar(200), F_LAST_BEAT_TIME timestamp(6)) DISTRIBUTED BY (F_DEVICE_ID);

CREATE TABLE smartap.T_DEVICE_CONTROL (F_ID varchar(32) PRIMARY KEY, F_DEVICE_ID varchar(32), F_GET_FILE varchar(100), F_PUT_FILE varchar(100), F_RESTART varchar(10), F_REBOOT varchar(10), F_UPDATE varchar(10), F_SHELL varchar(200), F_HAS_SYNC numeric DEFAULT 0, F_CLIENT_ON_TIME varchar(1000), F_CLIENT_OFF_TIME varchar(1000), F_CREATION_TIME timestamp(6)) DISTRIBUTED BY (F_ID);

CREATE TABLE smartap.T_DEVICE_MANUFACTURER (F_ID varchar(32) PRIMARY KEY, F_NAME varchar(70) NOT NULL, F_ORGANIZATION_CODE varchar(9) NOT NULL, F_ADDRESS varchar(256) NOT NULL, F_LINKMAN varchar(128) NOT NULL, F_TEL varchar(128) NOT NULL, F_MAIL varchar(32) NOT NULL, F_IS_AUDIT numeric(38, 0) DEFAULT 2, F_USER_ID varchar(32), F_AUDIT_USER_ID varchar(32), F_CREATION_TIME timestamp(6) NOT NULL, F_AUDIT_TIME timestamp(6), F_AREA_CODE numeric(38, 0), F_DES_KEY varchar(20), F_VI_KEY varchar(20), F_TCP_PORT numeric, F_UDP_PORT numeric) DISTRIBUTED BY (F_ID);

CREATE TABLE smartap.T_DICTIONARY (F_ID varchar(255) PRIMARY KEY, F_CREATE_DATE timestamp(6), F_CREATOR varchar(255), F_KEY varchar(255), F_ORDER numeric(10, 0), F_VALUE varchar(255), F_TYPE_ID varchar(255), F_DESC varchar(255)) DISTRIBUTED BY (F_ID);

CREATE TABLE smartap.T_LOCATION (F_LOCATION_ID varchar(32) PRIMARY KEY, F_USER_ID varchar(32) NOT NULL, F_ADDRESS_ID varchar(32), F_DEVICE_ID varchar(32), F_SITE_ID varchar(32), F_LONGITUDE varchar(20), F_LATITUDE varchar(20), F_CREATION_TIME timestamp(6) NOT NULL, F_FLOOR varchar(5), F_ISDEL numeric DEFAULT 0, F_AREA_CODE numeric(38, 0)) DISTRIBUTED BY (F_LOCATION_ID);

CREATE TABLE smartap.T_LOCATION_SERVER (F_LOCATION_SERVER_ID varchar(32) PRIMARY KEY, F_SERVER_ID varchar(32) NOT NULL, F_USER_ID varchar(32) NOT NULL, F_LOCATION_ID varchar(32) NOT NULL, F_CHANGE_TIME timestamp(6) NOT NULL, F_ENABLE_TIME timestamp(6) NOT NULL, F_STATUS numeric(38, 0) NOT NULL, F_LAST_BEAT_TIME timestamp(6), F_LAST_DATA_TIME timestamp(6)) DISTRIBUTED BY (F_LOCATION_SERVER_ID);

CREATE TABLE smartap.T_PASSENGER_OLD (F_SITE_CODE varchar(32) NOT NULL, F_SITE_NAME varchar(32), F_PASSENGER_NUMBER numeric DEFAULT 0, F_PASSENGER_MACS text, F_OLD_PERIOD numeric, F_UPDATE_TIME timestamp(6)) DISTRIBUTED BY (F_SITE_CODE);

CREATE TABLE smartap.T_SERVER (F_SERVER_ID varchar(32) PRIMARY KEY, F_USER_ID varchar(32), F_IP varchar(20) NOT NULL, F_PORT numeric(38, 0) NOT NULL, F_DESCRIPTION varchar(100), F_AREA_ID varchar(255), F_AREA_CODE numeric(38, 0)) DISTRIBUTED BY (F_SERVER_ID);

CREATE TABLE smartap.T_SITE (F_ID varchar(32) PRIMARY KEY, F_ADDRESS_ID varchar(32), F_CODE varchar(14) NOT NULL, F_NAME varchar(256) NOT NULL, F_TYPE varchar(1) NOT NULL, F_NATURE numeric(38, 0), F_OWNER varchar(128), F_ID_TYPE varchar(3), F_ID_NUM varchar(128), F_TEL varchar(128), F_NETWORK_ACCESS_WAY varchar(255), F_NETWORK_SUPPLIER varchar(2), F_NETWORK_ACCOUNT_OR_IP varchar(64), F_ORGANIZATION_CODE varchar(9) NOT NULL, F_OPENING_TIME varchar(5), F_ENDING_TIME varchar(5), F_OPERATING_STATUS numeric(38, 0), F_IS_AUDIT numeric(38, 0), F_LONGITUDE varchar(255) NOT NULL, F_LATITUDE varchar(255) NOT NULL, F_CREATE_TIME timestamp(6) NOT NULL, F_AREA_CODE numeric(38, 0)) DISTRIBUTED BY (F_ID);

CREATE TABLE smartap.T_TERMINAL_FEATURE (F_ID varchar(32) PRIMARY KEY, F_LOCATION_ID varchar(32) NOT NULL, F_CAP_TIME timestamp(6) NOT NULL, F_MAC varchar(20) NOT NULL, F_MAC_TYPE varchar(100), F_SIGNAL varchar(60) NOT NULL, F_TERMINAL_SSID_LIST varchar(2000), F_ID_TYPE numeric(38, 0), F_ID_CONTENT varchar(64), F_ACCESS_SSID varchar(256), F_ACCESS_CHANNEL varchar(2), F_DEVICE_MAC varchar(17), F_DEVICE_CODE varchar(21) NOT NULL, F_HOTSPOT_ENCRYPTION_TYPE varchar(2), F_X varchar(8), F_Y varchar(8), F_SITE_CODE varchar(14) NOT NULL, F_LONGITUDE varchar(20) NOT NULL, F_LATITUDE varchar(20) NOT NULL, F_HAS_FULLINDEX numeric(8, 0) DEFAULT 0, F_AREA_CODE numeric(38, 0) NOT NULL, F_CREATE_TIME timestamp(6) DEFAULT current_timestamp, F_ORG_CODE varchar(60) NOT NULL) DISTRIBUTED BY (F_ID);

CREATE TABLE smartap.T_USER (F_USER_ID varchar(255) PRIMARY KEY, F_ADDRESS varchar(200), F_AGE numeric(10, 0), F_CHINESE_NAME varchar(100) NOT NULL, F_CREATE_TIME timestamp(6), F_IS_DELETE numeric(1,0), F_PASSWORD varchar(100), F_PHONE varchar(100), F_PIC varchar(200), F_SEX varchar(10), F_TEL varchar(100), F_USERNAME varchar(50) NOT NULL, F_DUTY_ID varchar(255), F_HID varchar(60)) DISTRIBUTED BY (F_USER_ID);

CREATE TABLE smartap.T_VIRTUAL_IDENTITY (F_ID varchar(32) PRIMARY KEY, F_LOCATION_ID varchar(32) NOT NULL, F_DEVICE_CODE varchar(200), F_CLIENT_MAC varchar(20) NOT NULL, F_IMSI varchar(60), F_IMEI varchar(60), F_TEL varchar(600), F_WEIXIN varchar(60), F_TAOBAO varchar(200), F_EMAIL varchar(600), F_QQ varchar(60), F_QQ_HISTORY varchar(600), F_AREA_CODE numeric(38, 0) NOT NULL, F_ORG_CODE varchar(60) NOT NULL, F_ILLEGAL_APP varchar(100), F_BAIDU_ID varchar(200), F_JINGDONG_ID varchar(200), F_TENCENT_WB_ID varchar(200), F_SINA_ID varchar(200), F_YOUKU_ID varchar(200), F_MILIAO_ID varchar(200), F_XIECHENG_ID varchar(200), F_DIDI_ID varchar(200), F_MONO_ID varchar(200), F_DANGDANG_ID varchar(200), F_MEITUAN_ID varchar(200), F_DAZHONG_ID varchar(200), F_KUGOU_ID varchar(200), F_CAP_TIME timestamp(6) NOT NULL, F_CREATE_TIME timestamp(6) DEFAULT current_timestamp) DISTRIBUTED BY (F_ID);
